import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

export const fetchProducts = createAsyncThunk('posts/fetchPosts', async () => {
    const response = await fetch('/products.json');
    return response.data
})

const checkOutSlice = createSlice({
    name: "checkout",
    initialState: {
        list: [],
        loading: false
    },
    reducers: {
        productsRequested: (state, actions) => {
            state.loading = true;
        },
        productsReceived: (state, actions) => {
            state.loading = false;
            state.list = actions.payload;
        },
        productsFailed: (state, actions) => {
            state.loading = false;
        }
    }
});

export const { productsFailed, productsReceived, productsRequested } = checkOutReducer.actions;

export default checkOutSlice.reducer;
