import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

export const fetchProducts = createAsyncThunk('checkout/productsReceived', async () => {
    console.log("bjbhj")
    const response = await fetch('/products.json');
    console.log({response});
    return response.products;
});

const checkOutSlice = createSlice({
    name: "checkout",
    initialState: {
        list: [],
        loading: false
    },
    reducers: {
        productsRequested: (state, actions) => {
            state.loading = true;
        },
        productsReceived: (state, actions) => {
            state.loading = false;
            console.log({actions,state});
            state.list = actions.payload;
        },
        productsFailed: (state, actions) => {
            state.loading = false;
        }
    }
});

export const { productsFailed, productsReceived, productsRequested } = checkOutSlice.actions;

export default checkOutSlice.reducer;
