import { createSlice, createAsyncThunk, nanoid } from '@reduxjs/toolkit'

export const fetchProducts = createAsyncThunk('checkout/productsReceived', async () => {
    console.log("bjbhj")
    const response = await (await fetch('/products.json')).json();
    console.log({ response });
    return response;
});

const checkOutSlice = createSlice({
    name: "checkout",
    initialState: {
        list: [],
        loading: false
    },
    reducers: {
        productsReceived: {
            reducer(state, actions) {
                state.loading = false;
                console.log({ actions, state });
                state.list.push(actions.payload);
            }
        }

    }
});

export const { productsFailed, productsReceived, productsRequested } = checkOutSlice.actions;

export default checkOutSlice.reducer;
