import { createSlice, createAsyncThunk, nanoid } from '@reduxjs/toolkit'

export const fetchProducts = createAsyncThunk('checkout/productsReceived', async () => {
    console.log("bjbhj")
    const response = await (await fetch('/products.json')).json();
    console.log({ response });
    return response;
});

const checkOutSlice = createSlice({
    name: "checkout",
    initialState: {
        list: [],
        loading: false
    },
    reducers: {
    },
    extraReducers(builder) {
        builder
          .addCase(fetchProducts.pending, (state, action) => {
            state.status = 'loading'
          })
          .addCase(fetchProducts.fulfilled, (state, action) => {
            state.status = 'succeeded'
            // Add any fetched posts to the array
            state.posts = state.list.concat(action.payload)
          })
          .addCase(fetchProducts.rejected, (state, action) => {
            state.status = 'failed'
            state.error = action.error.message
          })
      }
});

export const { productsFailed, productsReceived, productsRequested } = checkOutSlice.actions;

export default checkOutSlice.reducer;
